import "./Card.css"
import { Link } from "react-router-dom"
import { Component } from "react"

class Card  extends Component {
    constructor(props){
        super(props)

        this.state={
            showExtra: false
        }
    }

    handleShowExtra(){
        this.setState({
            showExtra: !this.state.showExtra
        })
    }

    render(){
        const { name, descripcion, img, id , extra } = this.props
        return(
            <section class="card-container">
                <div className="character-card">
                    <img src={`/imgs/characters/${img}`} alt={name} />
                    <Link to={`/personaje/id/${id}`}><h4>{name}</h4></Link>
                    <p>{descripcion}</p>
                    <Link to={`/personaje/id/${id}`}>Ver detalle</Link>
                    <button onClick={() => this.handleShowExtra()}>this.state.showExtraVer extra</button>
                 </div>
            </section>
        )
    }

}

export default Card
