import React, { Component } from 'react';
import "./MovieDetail.css"

class MovieDetail extends Component {

    constructor(props){
        super(props)
        this.state = {
            movie: null,  
            isLoading: true,
            esFavorito: false,
        }
    }

    componentDidMount() {
        this.setState({
            isLoading: true
        })

        const {id} = this.props;

        fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=2e1ba77b764a76e2e48e86179135ae4d`)
            .then((response) => response.json())
            .then((data) => 
                this.setState({
                    movie: data,
                    isLoading: false
                })
            )
            .catch((error) => console.log(error));

    }

    agregarFavoritos() {
        const storage = localStorage.getItem('favoritos')
        if (storage !== null) {
            const parsedArray = JSON.parse(storage)
            parsedArray.push(this.props.infoMovie.id)
            const stringArray = JSON.stringify(parsedArray)
            localStorage.setItem('favoritos', stringArray)
        } else {
            const primerPelicula = [this.props.infoMovie.id]
            const stringArray = JSON.stringify(primerPelicula)
            localStorage.setItem('favoritos', stringArray)
        }
        this.setState({ esFavorito: true })
    }

    sacarFavoritos(id){
        const storage = localStorage.getItem('favoritos')

        if(storage){
          const parsedArray = JSON.parse(storage)
          const favoritosRestantes = parsedArray.filter(favId => favId !== id)
          const stringArray = JSON.stringify(favoritosRestantes)
          localStorage.setItem('favoritos', stringArray)

          if (favoritosRestantes.length > 0) {
            Promise.all(
                favoritosRestantes.map(id =>
                  fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=2e1ba77b764a76e2e48e86179135ae4d`)
                    .then(response => response.json())
                )
            ).then(data => {
                this.setState({
                    peliculas: data
                });
            });
          } else {
            this.setState({
              peliculas: []
            });
          }
        }
    }


    render() {
        const { movie, isLoading } = this.state

        if (isLoading) {
            return <p>Cargando...</p>
        }

        // destructuring de la info de movie
        const {id, poster_path,  title, genres, release_date, runtime, overview} = movie

        return (
            <article className="movie-detail-container">
                <section className='movie-detail'>
                    <img src= {`https://image.tmdb.org/t/p/w342/${poster_path}`} alt= {title} className='img'/>
                    <h2 className='h2'>Titulo: {title}</h2>
                    <p className='p'>Generos: {genres.map((genre) => genre.name).join(', ')}</p>
                    <p className='p'>Fecha de estreno: {release_date}</p>
                    <p className='p'>Duracion: {runtime} minutos</p>
                    <p className='sinopsis'>Sinopsis: </p>
                    <p className='p'>{overview}</p>
                    <div className='desc'>
                      <button onClick = {() => this.sacarFavoritos(id)}>
                        {!esFavorito ? 'Sacar de favoritos' : 'Agregar a favoritos'}
                      </button>
                  </div>

                </section>   
            </article>
        )

    }
}

export default MovieDetail
