import React, { Component } from "react";
import VerTodas from "../VerTodas/VerTodas";
import { options } from "../../options";


class Cartelera extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: [],
            filteredMovies: [],
            filterValue: "",
            actualPage: 1,
            isLoading: true
        }
    }

    componentDidMount() {
        this.setState({
            isLoading: true
        })

        fetch(`https://api.themoviedb.org/3/movie/now_playing?language=en-US&page=${this.state.actualPage}`, options)
            .then(response => response.json())
            .then((data) => {
                this.setState({
                    movies: data.results,
                    filteredMovies: data.results,
                    actualPage: this.state.actualPage + 1,
                    isLoading: false
                })
            })
    }

    handleResetFilter() {
        this.setState({
            filterValue: "",
            filteredMovies: this.state.movies
        })
    }

    handleLoadMore() {
        fetch(`https://api.themoviedb.org/3/movie/now_playing?language=en-US&page=${this.state.actualPage}`, options)
            .then(response => response.json())
            .then(data => {
                this.setState({
                    movies: this.state.movies.concat(data.results),
                    filteredMovies: this.state.movies.concat(data.results),
                    actualPage: this.state.actualPage + 1
                })
            })
    }

    handleFilterChange(e) {
        const userValue = e.target.value;

        this.setState({
            filterValue: userValue,
            filteredMovies: this.state.movies.filter(movie => movie.title.toLowerCase().includes(userValue.toLowerCase()))
        })
    }

    render() {
        return (
            <>
                <section className="cartelera-container">
                    <h1 className="titulo">Peliculas Populares</h1>
                    {this.state.isLoading ? <p>Cargando</p> :
                        <>
                            <div className="desc">
                                <label>Filtrar pelicula por nombre: </label>
                                <input type="text" onChange={(e) => this.handleFilterChange(e)} value={this.state.filterValue} className="filtrador"/>
                            </div>
                            <button onClick={() => this.handleResetFilter()}> Resetear el filtro </button>
                            <VerTodas movies={this.state.filteredMovies} />
                        </>
                    }
                    <div>
                        <button onClick={() => this.handleLoadMore()}> Cargar MÃ¡s </button>
                    </div>
                </section>
            </>
        )
    }

}

export default Cartelera
