import React, { Component } from 'react';
import Pelicula from '../components/Pelicula/Pelicula';

class SearchResults extends Component {
    constructor(props){
        super(props);

        this.state = {
            movies: [],
            isLoading: true,
            error: null
        }
    }

    componentDidMount(){
        this.setState({
            isLoading: true
        })
        fetch(`https://api.themoviedb.org/3/search/movie?query=${this.props.location.state.query}&api_key=2e1ba77b764a76e2e48e86179135ae4d`)
        .then((response) => response.json())
        .then((data) => {
            if (data.results) {
                this.setState({ 
                    movies: data.results, 
                    isLoading: false
                })
            } else {
                console.error('No se encontaron resultados para su busqueda')
            }
        })
        .catch((error) => {
            console.error('error', error)
            this.setState({ 
                isLoading: false
            })  
        })
    }

    render(){
        const {movies, isLoading, error} = this.state

        return(
            <>
                <section>
                    <h2> Resultados de busqueda: {this.props.location.state.query} </h2>
                    { isLoading ? (
